import React, { useState } from 'react'
import { Button, Alert, Badge, Navbar, Nav, NavDropdown, Row, Card } from 'react-bootstrap';

function Increment() {

    let [state, setState] = useState({
        count: 0
    });

    let inc = () => {
        setState ({
           count: state.count + 1
        })
    };
    let dec = () => {
        setState ({
           count: state.count - 1
        })
    };


    return (
        <div>
            <div className="row">
                <div className="col-2">
                    <Card className='shadow-lg' >
                        <p className='display-5' >Bitcoin</p>
                        <div className="col-10">
                            <button onClick={inc} className='btn btn-outline-danger mx-2'>Increment</button>
                            <button onclick={dec} className='btn btn-info'>Decrement</button>
                        </div>
                    </Card>
                </div>
            </div>
        </div>
    )
}

export default Increment
